import React, { useEffect } from 'react'
import { useState } from 'react'
import { Redirect } from 'react-router-dom'
import { Input } from 'reactstrap'
import { APIURL } from '../config'
import IdeaForm from './IdeaForm'
export default function IdeaCreate() {
    const initialTagState = []
    const initialIdeaState = {
        title: '',
        tags: initialTagState
    }
    const [idea, setIdea] = useState(initialIdeaState)
    const [tags, setTags] = useState(initialTagState)
    const [createdId, setCreatedId] = useState(null)
    const [error, setError] = useState(false)
    const handleChangeTags = (e) => {
        console.log(e)
        const isChecked = e.currentTarget.checked
        let newTagArr = tags
        if (isChecked) {
            newTagArr.push(e.currentTarget.value)
            setTags(newTagArr)
        } else {
            const index = tags.indexOf(e.currentTarget.value)
            newTagArr.splice(index, 1)
            setTags(newTagArr)
        }
    }
    const handleChange = (e) => {
        if (e.target === 'input.form-check-input') {
            console.log('checkbox')
            handleChangeTags(e)
        }

        e.persist()
        setIdea({
            ...idea,
            [e.target.name]: e.target.value
        })
        console.log(idea)
    }
    // const handleChangeTags = (e) => {
    //     const isChecked = e.target.checked
    //     let newTagArr = tags
    //     if (isChecked) {
    //         newTagArr.push(e.target.value)
    //         setTags(newTagArr)
    //     } else {
    //         const index = tags.indexOf(e.target.value)
    //         newTagArr.splice(index, 1)
    //         setTags(newTagArr)
    //     }
    // }
    const handleSubmit = (e) => {
        e.preventDefault()
        const url = `${APIURL}/ideas/`
        const stuffToSend = {
            method: 'POST',
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
                'Authorization': `Token ${localStorage.getItem('token')}`
            },
            body: JSON.stringify(idea),
            credentials: 'include'
        }
        console.log(stuffToSend)
        fetch(url, stuffToSend
        )
            .then(res => res.json())
            .then(data => {
                setCreatedId(data.id)
            })
            .catch(() => {
                setError(true)
            })
    }
    if (createdId) {
        return <Redirect to={`/ideas/${createdId}`} />
    }

    return (
        <div>
            <h1>What's your idea?</h1>
            {error && <div className="alert alert-danger">Sorry, something went wrong. Please try again.</div>}
            <IdeaForm idea={idea} handleChange={handleChange} handleSubmit={handleSubmit} handleChangeTags={handleChangeTags}></IdeaForm>
        </div>

    )
}

